CustomField.formula:
/* Check if 1 week prior to the ADBD #2 date is included in the 'Holiday List' custom label */
IF(CONTAINS( $Label.Holiday_List , TEXT(Advisory_Board_2__c - 7)) ,
/* If true, Subtract 1 day from ADBD #2 date before excluding weekends */
CASE(MOD((Advisory_Board_2__c - 8) - DATE(1900, 1, 7), 7), 0, (Advisory_Board_1__c - 10), /* Sunday */ 1, (Advisory_Board_1__c - 8), /* Monday */ 2, (Advisory_Board_1__c - 8), /* Tuesday */ 3, (Advisory_Board_1__c - 8), /* Wednesday */ 4, (Advisory_Board_1__c - 8), /* Thursday */ 5, (Advisory_Board_1__c - 8), /* Friday */ 6, (Advisory_Board_1__c - 9), /* Saturday */ null),
/* If false, just use Week Prior */
(Advisory_Board_2__c - 7)
)
