WebLink.url:
//  makes a simple ajax call to call download_event_registration()
var url = "https://cms.erepublic.com/common/queue/add?interface=salesforce&method=download_event_registration&bypass=1&salesforce_id={!Registration__c.Id}"

var xhttp = new XMLHttpRequest();
xhttp.onreadystatechange = function() {
    if (xhttp.readyState == 4 && xhttp.status == 200) {
        alert('result: '+ xhttp.responseText );
    }
};

xhttp.open("GET", url, true);
xhttp.send();
