ValidationRule.errorConditionFormula:
AND(
  Remaining_Deliverables__c = TRUE,
  OR(
    AND(
      NOT(ISPICKVAL( Project_Stage__c, "Completed")),
      ISBLANK( Event__r.Event_Start_Date__c )
    ),
    AND(
      NOT(ISPICKVAL( Project_Stage__c, "Completed")),
      Event__r.Event_Start_Date__c > TODAY()
    )
  )
)
