WebLink.url:
{!REQUIRESCRIPT('//code.jquery.com/jquery-2.0.3.min.js')}

var URL =
  "https://cms.erepublic.com/common/salesforce/salesforce_api/convert_sales_lead/?Id={!Sales_Lead__c.Id}&sfuser={!$User.Id}";

jQuery.ajax(
  {
    type: "GET",
    url: "/services/proxy",
    headers:
      {
        "SalesforceProxy-Endpoint" : URL
      },
    success :
      function(response) {
        var res = JSON.parse(response);
        if( res.JavaScript ) {
          eval(res.JavaScript);
        } else {
          console.log(res);
        }
      },
    error :
      function(response) {
        console.log(response);
      }
  }
);
